% select the hrf model
model = hrftd;
% set the second data
try
    if model == hrftd;
    sec = length(model(1,:)) ./ 32;
    opt = 1
    end
catch
    sec = length(model(1,:)) ./ 18;
    opt = 2
end


% create the signal run with convolution
signal = hrf_4;
run(1:(events(find(events > 0,1,'last')) + 64) .* sec) = 0;
for n = 1:length(events);
    run(round(events(n) .* sec):round((events(n) .* sec) + (length(signal(1,:)) - 1))) = ...
        run(round(events(n) .* sec):round((events(n) .* sec) + (length(signal(1,:)) - 1))) + signal;
end
figure(3)
plot(run)

% now create the convoluted model
model_con(1:length(model(:,1)),1:(events(find(events > 0,1,'last')) + 64) .* sec) = 0;
for n = 1:length(events);
    for m = 1:length(model(:,1));
        model_con(m,round(events(n) .* sec):round((events(n) .* sec) + (length(model(m,:)) - 1))) = ...
            model_con(m,round(events(n) .* sec):round((events(n) .* sec) + (length(model(m,:)) - 1))) + model(m,:);
    end
end

% add a column of ones
model_con(length(model(:,1)) + 1,:) = 1;

% run multiple regression
[b,bint,r,rint,stats] = regress(run',model_con');

% specify r_square
r_square = stats(1);
% specify betas (8th is the column of ones)
betas = b;

% plot the model
clear n
for n = 1:length(model(:,1));
    weight(n,:) = model(n,:) .* b(n);
end
total = sum(weight);
figure(1);
plot(signal);
